import { useState } from "react"; 

function App() {
  const [user, setUser] = useState("");
  const [info, setInfo] = useState({})
  const onChange = (event) => setUser(event.target.value);
  const onSubmit = async (event) => { 
    event.preventDefault();

    if(user === "") {
        return;
    }
    console.log(user)
    const userName = user;
    const url = `https://api.github.com/users/${userName}`
    fetch(url)
      .then(res => res.json())
      .then(data => {
        setInfo({
          name: data.name,
          bio: data.bio,
          followers: data.followers,
          following: data.following,
          repos: data.public_repos
        })
      })
      // console.log(data)
      console.log(info)
      setUser("")
  }

  return (
    <div>
      <h1>Github</h1>
      <form onSubmit={onSubmit}>
        <input placeholder="user name" type='text' value={user} onChange={onChange} />
        <button>click</button>
      </form>

      <h2>User Name: {info.name}</h2>

    </div>
  );
}

export default App;
