import { useState, useEffect } from "react"; 

function App() {
  const [user, setUser] = useState("");
  const [info, setInfo] = useState({})
  const onChange = (event) => setUser(event.target.value);
  const onSubmit = (event) => { 
    event.preventDefault();

    if(user === "") {
        return;
    }
    console.log(user)
    const userName = user;
    const url = `https://api.github.com/users/${userName}`
    fetch(url)
      .then(res => res.json())
      .then(data => {
        setInfo({
          avatar: data.avatar_url,
          name: data.name,
          bio: data.bio,
          followers: data.followers,
          following: data.following,
          repos: data.public_repos
        })
        console.log(data)
      })
      setUser("")

    }
    useEffect(()=>{
      console.log(info)
    }, [info]) 


  return (
    <div>
      <h1>Github</h1>
      <form onSubmit={onSubmit}>
        <input placeholder="user name" type='text' value={user} onChange={onChange} />
        <button>click</button>
      </form>
      <div className="userInfomation" class="">
        <img src={info.avatar} alt="프로필 사진" ></img>
        <h2>User Name: {info.name}</h2>
      </div>

    </div>
  );
}

export default App;
