import { useState } from "react"; 

function App() {
  const [user, setUser] = useState("");
  const onChange = (event) => setUser(event.target.value);
  const onSubmit = (event) => {
      event.preventDefault();

      if(user === "") {
        return;
      }
      console.log(user)
      const userName = user;
      const url = `https://api.github.com/users/${userName}`
      fetch(url)
        .then(res => res.json())
        .then(data => {
          const userInfo = [{name: data.name, bio: data.bio, repos: data.public_repos}]
          console.log(data)
          console.log(userInfo)
        })
      setUser("")
  }



  return (
    <div>
      <h1>Github</h1>
      <form onSubmit={onSubmit}>
        <input placeholder="user name" type='text' value={user} onChange={onChange} />
        <button>click</button>
      </form>

    </div>
  );
}

export default App;
